import React, { Component } from 'react';

class InputComponent extends React.Component{ 
    constructor(props){ 
        super(props); 

        this.state = {
            name: '',
            price: 0,
            onSale: null
        }
      }
onChange = (e)=>{ 
    this.setState({ name: e.target.value })

}
onChangePrice = (e)=>{ 
    this.setState({ price: e.target.value })

}

render(){ 
    return (
        <form onSubmit={this.props.addItem(this.state.item)}> 
            <label> 
                Input Item: 
                <input type="text" name= 'name' value = {this.state.name} onChange ={this.onChange} />
            </label>
            <label>
                Price:
                <input type="text" name="price" value = {this.state.price} onChange ={this.onChangePrice}/>
            </label>
            <label>
                On Sale:
                <input type="checkbox" name="onSale" onChange={e => console.log(e)} />
            </label>
            <button type="submit" name="Create"> Add Item</button>
        </form>
    ); 
}
}

export default InputComponent;
